// define your global instances of motors and other devices here
void correct_orientation(int correction){
  cout << "heh" << endl;
  while(Inertial1.rotation(degrees) < correction - 10){
    cout << Inertial1.rotation(degrees) << endl;
    FrontLeft.spin(vex::forward, 30, percent);
    FrontRight.spin(vex::reverse, 30, percent);
    MiddleLeft.spin(vex::forward, 30, percent);
    MiddleRight.spin(vex::reverse, 30, percent);
    BackLeft.spin(vex::forward, 30, percent);
    BackRight.spin(vex::reverse, 30, percent);
    wait(20, msec);
  }
  while(Inertial1.rotation(degrees) > correction + 10){
    cout << Inertial1.rotation(degrees) << endl;
    FrontLeft.spin(vex::reverse, 30, percent);
    FrontRight.spin(vex::forward, 30, percent);
    MiddleLeft.spin(vex::reverse, 30, percent);
    MiddleRight.spin(vex::forward, 30, percent);
    BackLeft.spin(vex::reverse, 30, percent);
    BackRight.spin(vex::forward, 30, percent);
    wait(20, msec);
  }
  FrontLeft.stop(brake);
  FrontRight.stop(brake);
  MiddleLeft.stop(brake);
  MiddleRight.stop(brake);
  BackLeft.stop(brake);
  BackRight.stop(brake);
  wait(20, msec);
}

void process_save(string save){
  while (1) {
    if(save.length() != 0){
      // spin chassis
      go = (save[0] - 48) * 10 + save[1] - 48;
      rotate = (save[2] - 48) * 10 + save[3] - 48;
      wing1 = save[4];
      wing2 = save[5];
      if(go != 49 || rotate != 49){
        if(rotate == 49){
          FrontLeft.spin(vex::forward, go * 2 - 99, percent);
          FrontRight.spin(vex::forward, go * 2 - 99, percent);
          MiddleLeft.spin(vex::forward, go * 2 - 99, percent);
          MiddleRight.spin(vex::forward, go * 2 - 99, percent);
          BackLeft.spin(vex::forward, go * 2 - 99, percent);
          BackRight.spin(vex::forward, go * 2 - 99, percent);
        }
        else if(go == 49){
          FrontLeft.spin(vex::forward, (rotate * 2 - 99) / 1.5, percent);
          FrontRight.spin(vex::forward, -(rotate * 2 - 99) / 1.5, percent);
          MiddleLeft.spin(vex::forward, (rotate * 2 - 99) / 1.5, percent);
          MiddleRight.spin(vex::forward, -(rotate * 2 - 99) / 1.5, percent);
          BackLeft.spin(vex::forward, (rotate * 2 - 99) / 1.5, percent);
          BackRight.spin(vex::forward, -(rotate * 2 - 99) / 1.5, percent);
        }
        else{
          FrontLeft.spin(vex::forward, ((go * 2 - 99)) + ((rotate * 2 - 99) / 1), percent);
          FrontRight.spin(vex::forward, ((go * 2 - 99)) - ((rotate * 2 - 99) / 1), percent);
          MiddleLeft.spin(vex::forward, ((go * 2 - 99)) + ((rotate * 2 - 99) / 1), percent);
          MiddleRight.spin(vex::forward, ((go * 2 - 99)) - ((rotate * 2 - 99) / 1), percent);
          BackLeft.spin(vex::forward, ((go * 2 - 99)) + ((rotate * 2 - 99) / 1), percent);
          BackRight.spin(vex::forward, ((go * 2 - 99)) - ((rotate * 2 - 99) / 1), percent);
        }
      }
      else{
        FrontLeft.stop(brake);
        FrontRight.stop(brake);
        MiddleLeft.stop(brake);
        MiddleRight.stop(brake);
        BackLeft.stop(brake);
        BackRight.stop(brake);
      }
      if(wing1 == "1"){
        Flap1.set(true);
      }
      else{
        Flap1.set(false);
      }
      if(wing2 == "1"){
        Flap2.set(true);
      }
      else{
        Flap2.set(false);
      }
      //delete read parts of save file
      for (int i = 0; i < 6; i++) {
        save.erase(save.begin());
      }
    }
    else{
      FrontLeft.stop(brake);
      FrontRight.stop(brake);
      MiddleLeft.stop(brake);
      MiddleRight.stop(brake);
      BackLeft.stop(brake);
      BackRight.stop(brake);
      break;
    }
    wait(20, msec);
  }
}
