void autonomous(void) {
  Inertial1.calibrate();
  cout << "0" << endl;
  Catapult.setVelocity(90, percent);
  cout << "1" << endl;
  while(count > 0){
    count -= 1;
    Catapult.spinFor(vex::forward, 1, turns);
    wait(50, msec);
    cout << "2" << endl;
  }
  cout << "this" << endl;
  Catapult.spinFor(vex::forward, 0.5, turns);
  process_save(save1);
  correct_orientation(correction1);
  process_save(save2);
  correct_orientation(correction2);
  process_save(save3);
}

